import java.util.*;

public class IPLMatch {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Team 1 input
        String team1 = sc.nextLine();
        int[] scores1 = new int[11];
        int sum1 = 0;
        for (int i = 0; i < 11; i++) {
            scores1[i] = sc.nextInt();
            sum1 += scores1[i];
        }

        sc.nextLine(); // consume newline

        // Team 2 input
        String team2 = sc.nextLine();
        int[] scores2 = new int[11];
        int sum2 = 0;
        for (int i = 0; i < 11; i++) {
            scores2[i] = sc.nextInt();
            sum2 += scores2[i];
        }

        // Find man of the match (highest score among all players)
        int maxScore = Math.max(Arrays.stream(scores1).max().getAsInt(),
                                Arrays.stream(scores2).max().getAsInt());

        // Decide winner
        String winner = (sum1 > sum2) ? team1 : team2;

        // Output
        System.out.println(winner);
        System.out.println(maxScore);
    }
}

