import java.util.*;

public class TCSGems {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int N = sc.nextInt(); // number of test cases

        for (int t = 0; t < N; t++) {
            int C = sc.nextInt();   // value of gems
            int I = sc.nextInt();   // number of items
            int[] arr = new int[I];

            for (int i = 0; i < I; i++) {
                arr[i] = sc.nextInt();
            }

            // HashMap to store value -> index
            Map<Integer, Integer> map = new HashMap<>();

            int idx1 = -1, idx2 = -1;

            for (int i = 0; i < I; i++) {
                int price = arr[i];
                int complement = C - price;

                if (map.containsKey(complement)) {
                    idx1 = map.get(complement) + 1; // convert to 1-based index
                    idx2 = i + 1;
                    break;
                }
                map.put(price, i);
            }

            // Ensure lower index comes first
            if (idx1 < idx2) {
                System.out.println(idx1 + " " + idx2);
            } else {
                System.out.println(idx2 + " " + idx1);
            }
        }
    }
}

