import java.util.*;

public class DoubloonChecker {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt();  // number of words
        sc.nextLine(); // consume newline

        for (int i = 0; i < n; i++) {
            String word = sc.nextLine();
            if (isDoubloon(word)) {
                System.out.println(word + " is a doubloon");
            } else {
                System.out.println(word + " is not a doubloon");
            }
        }
    }

    // Function to check if word is a doubloon
    public static boolean isDoubloon(String word) {
        word = word.toLowerCase();  // case insensitive
        Map<Character, Integer> freq = new HashMap<>();

        for (char c : word.toCharArray()) {
            freq.put(c, freq.getOrDefault(c, 0) + 1);
        }

        for (int count : freq.values()) {
            if (count != 2) return false;
        }
        return true;
    }
}

