public class StringLengthRecursive {

    // Recursive function to find length of string
    static int length(String s) {
        // Base case: empty string
        if (s.equals("")) {
            return 0;
        }
        // Recursive case: 1 + length of substring (excluding first char)
        return 1 + length(s.substring(1));
    }

    public static void main(String[] args) {
        String str = "recursion";
        System.out.println("Length of \"" + str + "\" is: " + length(str));
    }
}

