import java.util.Scanner;

class PalinCheck {
    int num;        // number to check
    int numReverse; // reversed number

    // Constructor to initialize variables
    PalinCheck() {
        num = 0;
        numReverse = 0;
    }

    // Method to get number from user
    void getNumber() {
        Scanner sc = new Scanner(System.in);
        System.out.print("Enter a number: ");
        num = sc.nextInt();
    }

    // Recursive function to reverse number
    int revNum(int y) {
        if (y == 0) {
            return 0;
        } else {
            int digits = (int) Math.log10(y);
            return (y % 10) * (int) Math.pow(10, digits) + revNum(y / 10);
        }
    }

    // Method to check palindrome
    void checkPalin() {
        numReverse = revNum(num);
        if (num == numReverse) {
            System.out.println(num + " is a palindrome");
        } else {
            System.out.println(num + " is not a palindrome");
        }
    }

    // Main method
    public static void main(String[] args) {
        PalinCheck pc = new PalinCheck();
        pc.getNumber();
        pc.checkPalin();
    }
}

