public class RecursiveSearch {

    // Recursive function to find element
    static int findElement(int[] arr, int start, int value) {
        if (start >= arr.length) { // base case: reached end
            return -1;
        }
        if (arr[start] == value) { // found the value
            return start;
        }
        return findElement(arr, start + 1, value); // recursive call
    }

    public static void main(String[] args) {
        int[] arr1 = {1, 2, 3, 4, 5};
        int[] arr2 = {10, 15, 20, 25, 30, 35, 40};

        System.out.println(findElement(arr1, 0, 3)); // Output: 2
        System.out.println(findElement(arr2, 0, 15)); // Output: 1
        System.out.println(findElement(arr1, 0, 7)); // Output: -1
    }
}

