import java.util.Scanner;

public class Election {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Number of candidates
        int n = sc.nextInt();

        // Array to store votes for each candidate (index 0 unused)
        int[] votes = new int[n + 1];

        while (true) {
            int vote = sc.nextInt();
            if (vote == 0) break; // stop input
            if (vote >= 1 && vote <= n) {
                votes[vote]++;
            }
        }

        // Find candidate with highest votes
        int maxVotes = 0;
        int winner = 0;
        for (int i = 1; i <= n; i++) {
            if (votes[i] > maxVotes) {
                maxVotes = votes[i];
                winner = i;
            }
        }

        // Print winner and vote count
        System.out.println(winner);
        System.out.println(maxVotes);
    }
}

