import java.util.Scanner;

public class ArrayClassifier {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt(); // size of array
        int[] arr = new int[n];

        for (int i = 0; i < n; i++) {
            arr[i] = sc.nextInt();
        }

        String type = classifyArray(arr);
        System.out.println(n + " " + type);
    }

    // Method to classify the array
    public static String classifyArray(int[] arr) {
        boolean increasing = true, decreasing = true;

        // Check if purely increasing
        for (int i = 1; i < arr.length; i++) {
            if (arr[i] < arr[i - 1]) {
                increasing = false;
                break;
            }
        }
        if (increasing) return "Increasing";

        // Check if purely decreasing
        for (int i = 1; i < arr.length; i++) {
            if (arr[i] > arr[i - 1]) {
                decreasing = false;
                break;
            }
        }
        if (decreasing) return "Decreasing";

        // Check for Increasing then Decreasing
        int i = 1;
        while (i < arr.length && arr[i] > arr[i - 1]) i++;
        if (i > 1) {
            boolean dec = true;
            for (int j = i; j < arr.length; j++) {
                if (arr[j] > arr[j - 1]) {
                    dec = false;
                    break;
                }
            }
            if (dec) return "Increasing then Decreasing";
        }

        // If not above, it must be Decreasing then Increasing
        return "Decreasing then Increasing";
    }
}

