import java.util.Scanner;

public class ArrayShift {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt(); // size of array
        int[] arr = new int[n];

        // Read array elements
        for (int i = 0; i < n; i++) {
            arr[i] = sc.nextInt();
        }

        int k = sc.nextInt(); // positions to shift
        k = k % n; // handle k > n

        // Create a new array for result
        int[] result = new int[n];

        // Shift elements
        for (int i = 0; i < n; i++) {
            result[i] = arr[(i + k) % n];
        }

        // Print shifted array
        for (int i = 0; i < n; i++) {
            System.out.print(result[i] + " ");
        }
    }
}
import java.util.Scanner;

public class PrimeIndexSwitch {
    // Function to check if a number is prime
    static boolean isPrime(int num) {
        if (num <= 1) return false;
        for (int i = 2; i * i <= num; i++) {
            if (num % i == 0) return false;
        }
        return true;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt(); // size of array
        int[] arr = new int[n];

        for (int i = 0; i < n; i++) {
            arr[i] = sc.nextInt();
        }

        int count = 0;

        for (int i = 0; i < n; i++) {
            int index = i;
            int elem = arr[i];

            // Only consider prime factors
            if (index > 0 && elem % index == 0 && isPrime(index)) {
                arr[i] = index;
                count++;
            }
        }

        // Print number of elements switched
        System.out.println(count);

        // Print switched array
        for (int i = 0; i < n; i++) {
            System.out.print(arr[i] + " ");
        }
    }
}

