import java.util.Scanner;

public class NextPalindrome {

    // Method to check if a string is a palindrome
    static boolean isPalindrome(String s) {
        int n = s.length();
        for (int i = 0; i < n / 2; i++) {
            if (s.charAt(i) != s.charAt(n - i - 1))
                return false;
        }
        return true;
    }

    // Method to get the next palindrome larger than the input
    static String nextPalindrome(String num) {
        int length = num.length();
        String next = num;

        while (true) {
            // Increment the number by 1
            next = addOne(next);
            // Check if it's palindrome
            if (isPalindrome(next)) {
                return next;
            }
        }
    }

    // Helper method to add 1 to a big number represented as string
    static String addOne(String num) {
        StringBuilder sb = new StringBuilder(num);
        int carry = 1;
        for (int i = sb.length() - 1; i >= 0; i--) {
            int digit = sb.charAt(i) - '0' + carry;
            sb.setCharAt(i, (char)((digit % 10) + '0'));
            carry = digit / 10;
        }
        if (carry > 0) {
            sb.insert(0, '1');
        }
        return sb.toString();
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String K = sc.next();
        System.out.println(nextPalindrome(K));
    }
}

