import java.util.Scanner;

public class LongestVowelessSubstring {

    // Method to check if a character is a vowel
    static boolean isVowel(char c) {
        c = Character.toLowerCase(c);
        return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String str = sc.nextLine();

        int maxLen = 0;
        int startIndex = 0;
        int currentStart = 0;
        int currentLen = 0;

        for (int i = 0; i < str.length(); i++) {
            if (!isVowel(str.charAt(i))) {
                if (currentLen == 0) {
                    currentStart = i; // Start of new voweless substring
                }
                currentLen++;
            } else {
                // Check if current voweless substring is the longest
                if (currentLen > maxLen) {
                    maxLen = currentLen;
                    startIndex = currentStart;
                }
                currentLen = 0; // Reset length for next substring
            }
        }

        // Final check in case the string ends with a voweless substring
        if (currentLen > maxLen) {
            maxLen = currentLen;
            startIndex = currentStart;
        }

        String longestVoweless = str.substring(startIndex, startIndex + maxLen);
        System.out.println(longestVoweless);
    }
}

