import java.util.HashSet;

public class IgniteFriends {

    // Method to get digits of a number as a set
    static HashSet<Character> getDigits(int num) {
        HashSet<Character> set = new HashSet<>();
        for (char c : String.valueOf(num).toCharArray()) {
            set.add(c);
        }
        return set;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n1 = sc.nextInt();
        int n2 = sc.nextInt();

        // Check if both numbers are 2-digit numbers
        if (n1 < 10 || n1 > 99 || n2 < 10 || n2 > 99) {
            System.out.println("Invalid Input");
            return;
        }

        // Get digits of both numbers
        HashSet<Character> digits = getDigits(n1);
        digits.addAll(getDigits(n2));

        // Get digits of product
        int product = n1 * n2;
        HashSet<Character> productDigits = getDigits(product);

        // Check if digits match
        if (digits.equals(productDigits)) {
            System.out.println(n1 + " and " + n2 + " are Ignite Friends");
        } else {
            System.out.println(n1 + " and " + n2 + " are not Ignite Friends");
        }
    }
}

