import java.util.*;

public class ConsecutiveSequence {

    // Method to check if array contains consecutive integers
    static boolean isConsecutive(int[] arr) {
        if (arr.length <= 1) return true;

        boolean ascending = arr[1] - arr[0] == 1;
        boolean descending = arr[1] - arr[0] == -1;

        for (int i = 1; i < arr.length; i++) {
            if (ascending && arr[i] - arr[i - 1] != 1) return false;
            if (descending && arr[i] - arr[i - 1] != -1) return false;
            if (!ascending && !descending) return false;
        }

        return true;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int t = sc.nextInt(); // Number of test cases
        while (t-- > 0) {
            int n = sc.nextInt(); // Number of elements
            int[] arr = new int[n];
            for (int i = 0; i < n; i++) {
                arr[i] = sc.nextInt();
            }

            System.out.println(isConsecutive(arr));
        }
    }
}

