public class SecretSandwich {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String str = sc.nextLine();
        boolean found = false;

        // Try all possible wrapper lengths from 1 to half of string length
        for (int len = 1; len <= str.length() / 2; len++) {
            String start = str.substring(0, len);
            String end = str.substring(str.length() - len);
            StringBuilder sb = new StringBuilder(end);

            if (start.equals(sb.reverse().toString())) {
                // Found wrapper, extract text in between
                String middle = str.substring(len, str.length() - len);
                System.out.println(middle);
                found = true;
                break;
            }
        }

        if (!found) {
            System.out.println("Invalid String");
        }
    }
}
