public class MakeAnagrams {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String a = sc.nextLine();
        String b = sc.nextLine();

        // Count frequency of each character in both strings
        int[] countA = new int[26];
        int[] countB = new int[26];

        for (char c : a.toCharArray()) countA[c - 'a']++;
        for (char c : b.toCharArray()) countB[c - 'a']++;

        // Build deleted characters
        StringBuilder delA = new StringBuilder();
        StringBuilder delB = new StringBuilder();
        int totalDeletions = 0;

        // Check deletions in string a
        for (char c : a.toCharArray()) {
            if (countB[c - 'a'] > 0) {
                countB[c - 'a']--; // Match with b
            } else {
                delA.append(c); // Needs deletion
                totalDeletions++;
            }
        }

        // Check deletions in string b
        for (char c : b.toCharArray()) {
            if (countA[c - 'a'] > 0) {
                countA[c - 'a']--; // Match with a
            } else {
                delB.append(c); // Needs deletion
                totalDeletions++;
            }
        }

        // Print output
        System.out.println(delA.toString());
        System.out.println(delB.toString());
        System.out.println(totalDeletions);
    }
}

