import java.util.Scanner;

public class KaprekarCheck {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();

        if (isKaprekar(n)) {
            System.out.println(n + " is a kaprekar number");
        } else {
            System.out.println(n + " is not a kaprekar number");
        }
    }

    static boolean isKaprekar(int n) {
        if (n == 1) return true; // 1 is a Kaprekar number

        int d = String.valueOf(n).length();
        long square = (long) n * n; // Use long to avoid overflow
        String str = String.valueOf(square);

        // Split the square into left and right parts
        int len = str.length();
        String rightPart = str.substring(len - d);
        String leftPart = len - d > 0 ? str.substring(0, len - d) : "0";

        int sum = Integer.parseInt(leftPart) + Integer.parseInt(rightPart);
        return sum == n;
    }
}

