public class MinOperationsToBalance {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.nextLine();

        int open = 0;  // Count of unmatched '{'
        int close = 0; // Count of unmatched '}'

        for (char c : s.toCharArray()) {
            if (c == '{') {
                open++; // an opening brace adds to unmatched '{'
            } else { // c == '}'
                if (open > 0) {
                    open--; // match with a previous '{'
                } else {
                    close++; // unmatched '}', needs to be replaced
                }
            }
        }

        // Each unmatched '{' or '}' needs half replacements
        int operations = (open + 1) / 2 + (close + 1) / 2;

        System.out.println(operations);
    }
}

