                int n = c - '0'; // convert char to int
                char nextChar = input.charAt(i + 1);
                // Repeat nextChar n times
                for (int j = 0; j < n; j++) {
                    result.append(nextChar);
                }
            } else {
                result.append(c);
            }
        }

        System.out.println(result.toString());
    }
}

