import java.util.*;

public class ArrangeWordsByLetter {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String line = sc.nextLine();
        String[] words = line.split(" ");

        List<String> wordList = new ArrayList<>(Arrays.asList(words));
        List<String> arranged = new ArrayList<>();

        // Start with the first word
        arranged.add(wordList.get(0));
        wordList.remove(0);

        while (!wordList.isEmpty()) {
            boolean found = false;
            String lastWord = arranged.get(arranged.size() - 1);
            char lastChar = lastWord.charAt(lastWord.length() - 1);

            for (int i = 0; i < wordList.size(); i++) {
                String word = wordList.get(i);
                if (word.charAt(0) == lastChar) {
                    arranged.add(word);
                    wordList.remove(i);
                    found = true;
                    break;
                }
            }

            // If no matching word found, just append the next word
            if (!found) {
                arranged.add(wordList.get(0));
                wordList.remove(0);
            }
        }

        // Print the arranged words
        for (String w : arranged) {
            System.out.print(w + " ");
        }
    }
}

