import java.util.Scanner;

class PigLatin {
    // Instance variables
    private String Txt;  // original word
    private String Cxt;  // piglatin word
    private int len;     // length of the word

    // Constructor to initialize instance variables
    public PigLatin() {
        Txt = "";
        Cxt = "";
        len = 0;
    }

    // Method to accept word from user
    public void getWord() {
        Scanner sc = new Scanner(System.in);
        System.out.print("Enter a word: ");
        Txt = sc.next();
        len = Txt.length();
    }

    // Method to convert word to Pig Latin
    public void convert2PigLatin() {
        String upperTxt = Txt.toUpperCase(); // convert to uppercase for uniformity
        char firstChar = upperTxt.charAt(0);

        // Check if first character is a vowel
        if (isVowel(firstChar)) {
            Cxt = upperTxt + "Y"; // Rule i
            return;
        }

        // Find the first vowel in the word
        int vowelPos = -1;
        for (int i = 1; i < len; i++) {
            if (isVowel(upperTxt.charAt(i))) {
                vowelPos = i;
                break;
            }
        }

        if (vowelPos != -1) {
            // Rule ii: split and rearrange
            String part1 = upperTxt.substring(vowelPos);
            String part2 = upperTxt.substring(0, vowelPos);
            Cxt = part1 + part2 + "D";
        } else {
            // Rule iii: no vowels
            Cxt = upperTxt + "N";
        }
    }

    // Helper method to check if a character is a vowel
    private boolean isVowel(char ch) {
        return "AEIOU".indexOf(ch) != -1;
    }

    // toString method to return original and Pig Latin word
    public String toString() {
        return "Original word: " + Txt + "\nPig Latin: " + Cxt;
    }

    // Main method to test
    public static void main(String[] args) {
        PigLatin pl = new PigLatin();
        pl.getWord();
        pl.convert2PigLatin();
        System.out.println(pl.toString());
    }
}

