public class CrazyNumber {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Number of test cases
        int t = sc.nextInt();

        for (int i = 0; i < t; i++) {
            long num = sc.nextLong();
            if (isCrazyNumber(num)) {
                System.out.println(num + " is a crazy number");
            } else {
                System.out.println(num + " is not a crazy number");
            }
        }
    }

    // Method to check if a number is Crazy
    public static boolean isCrazyNumber(long n) {
        String str = String.valueOf(n);
        int digits = str.length();
        long sum = 0;

        for (int i = 0; i < digits; i++) {
            int digit = str.charAt(i) - '0';
            sum += power(digit, digits);
        }

        return sum == n;
    }

    // Custom method to calculate power to avoid floating point errors
    public static long power(int base, int exp) {
        long result = 1;
        for (int i = 0; i < exp; i++) {
            result *= base;
        }
        return result;
    }
}

