import java.util.*;

public class PendulumArrangement {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt();
        int[] arr = new int[n];

        for (int i = 0; i < n; i++) {
            arr[i] = sc.nextInt();
        }

        Arrays.sort(arr);  // Sort ascending

        int[] pendulum = new int[n];
        int mid = n / 2;   // Middle index
        int left = mid - 1;
        int right = mid + 1;

        pendulum[mid] = arr[0];  // Smallest in center

        boolean goRight = true;
        for (int i = 1; i < n; i++) {
            if (goRight) {
                pendulum[right++] = arr[i];
            } else {
                pendulum[left--] = arr[i];
            }
            goRight = !goRight; // Alternate sides
        }

        // Print result
        for (int x : pendulum) {
            System.out.print(x + " ");
        }
    }
}

