import java.util.*;

public class DigitCounter {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt();
        int[] freq = new int[10];  // digit frequency from 09

        for (int i = 0; i < n; i++) {
            int num = sc.nextInt();
            String s = String.valueOf(num);
            for (char ch : s.toCharArray()) {
                if (Character.isDigit(ch)) {
                    freq[ch - '0']++;
                }
            }
        }

        // Find most occurred digit (if tie, take greater one)
        int mostDigit = -1, maxFreq = -1;
        for (int d = 0; d <= 9; d++) {
            if (freq[d] > maxFreq || (freq[d] == maxFreq && d > mostDigit)) {
                maxFreq = freq[d];
                mostDigit = d;
            }
        }

        // Collect digits that never occurred
        List<Integer> notOccurred = new ArrayList<>();
        for (int d = 0; d <= 9; d++) {
            if (freq[d] == 0) {
                notOccurred.add(d);
            }
        }

        // Output
        System.out.println("Most occurred digit : " + mostDigit);
        System.out.print("Digits that did not occur : ");
        for (int i = 0; i < notOccurred.size(); i++) {
            System.out.print(notOccurred.get(i));
            if (i < notOccurred.size() - 1) System.out.print(",");
        }
    }
}

