import java.util.*;

public class SentenceAnagram {
    // Helper to clean a sentence
    private static String normalize(String s) {
        // Remove ending punctuation
        if (s.endsWith(".") || s.endsWith("?") || s.endsWith("!")) {
            s = s.substring(0, s.length() - 1);
        }
        // Remove all non-alphabetic chars and convert to lowercase
        s = s.replaceAll("[^a-zA-Z]", "").toLowerCase();
        return s;
    }

    // Helper to check if two strings are anagrams
    private static boolean areAnagrams(String s1, String s2) {
        char[] a1 = s1.toCharArray();
        char[] a2 = s2.toCharArray();
        Arrays.sort(a1);
        Arrays.sort(a2);
        return Arrays.equals(a1, a2);
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        String sentence1 = sc.nextLine();
        String sentence2 = sc.nextLine();

        String normalized1 = normalize(sentence1);
        String normalized2 = normalize(sentence2);

        if (areAnagrams(normalized1, normalized2)) {
            System.out.println(sentence1 + " is an anagram of " + sentence2);
        } else {
            System.out.println(sentence1 + " is not an anagram of " + sentence2);
        }
    }
}

