import java.util.*;

public class SaddlePoint {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int m = sc.nextInt();
        int n = sc.nextInt();

        int[][] matrix = new int[m][n];

        // Read matrix
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                matrix[i][j] = sc.nextInt();
            }
        }

        int saddlePoint = -1;

        // Check each row
        for (int i = 0; i < m; i++) {
            // Find min element in row i
            int minVal = matrix[i][0];
            int colIndex = 0;
            for (int j = 1; j < n; j++) {
                if (matrix[i][j] < minVal) {
                    minVal = matrix[i][j];
                    colIndex = j;
                }
            }

            // Check if minVal is the maximum in its column
            boolean isSaddle = true;
            for (int k = 0; k < m; k++) {
                if (matrix[k][colIndex] > minVal) {
                    isSaddle = false;
                    break;
                }
            }

            if (isSaddle) {
                saddlePoint = minVal;
                break; // unique, so stop searching
            }
        }

        System.out.println(saddlePoint);
    }
}

