import java.util.*;

public class SecretCodeEncryption {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt();
        sc.nextLine(); // consume newline
        String input = sc.nextLine();

        if (n >= 10 || n <= 0) {
            System.out.println("Array's column size of " + n + " is Invalid");
            return;
        }

        // Step 1: Replace spaces with '*'
        input = input.replace(' ', '*');

        // Step 2: Find number of rows
        int length = input.length();
        int rows = (int) Math.ceil((double) length / n);

        // Step 3: Fill matrix row by row
        char[][] matrix = new char[rows][n];
        int index = 0;

        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < n; j++) {
                if (index < length) {
                    matrix[i][j] = input.charAt(index++);
                } else {
                    matrix[i][j] = '.'; // fill extra with '.'
                }
            }
        }

        // Step 4: Read column by column
        StringBuilder encrypted = new StringBuilder();
        for (int j = 0; j < n; j++) {
            for (int i = 0; i < rows; i++) {
                encrypted.append(matrix[i][j]);
            }
        }

        // Step 5: Output with n in front
        System.out.println(n + encrypted.toString());
    }
}

