        if (s1.length() != s2.length()) return false;

        int[] freq = new int[26]; // only lowercase letters a-z

        for (char c : s1.toCharArray()) {
            freq[c - 'a']++;
        }
        for (char c : s2.toCharArray()) {
            freq[c - 'a']--;
        }

        for (int f : freq) {
            if (f != 0) return false;
        }
        return true;
    }
}

