import java.util.Scanner;

public class CircularPrime {

    // Check if a number is prime
    public static boolean isPrime(int n) {
        if (n < 2) return false;
        for (int i = 2; i * i <= n; i++) {
            if (n % i == 0) return false;
        }
        return true;
    }

    // Generate rotations and check if all are prime
    public static boolean isCircularPrime(int n) {
        String numStr = String.valueOf(n);
        int len = numStr.length();

        String rotated = numStr;
        for (int i = 0; i < len; i++) {
            int rotatedNum = Integer.parseInt(rotated);
            if (!isPrime(rotatedNum)) {
                return false;
            }
            // rotate left: move first char to end
            rotated = rotated.substring(1) + rotated.charAt(0);
        }
        return true;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int num = sc.nextInt();

        if (isCircularPrime(num)) {
            System.out.println(num + " is a circular prime");
        } else {
            System.out.println(num + " is not a circular prime");
        }

        sc.close();
    }
}

