import java.util.*;

public class NumberToWords {

    // Arrays for word mapping
    private static final String[] belowTwenty = {
        "", "one", "two", "three", "four", "five", "six", "seven",
        "eight", "nine", "ten", "eleven", "twelve", "thirteen",
        "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen"
    };

    private static final String[] tens = {
        "", "", "twenty", "thirty", "forty", "fifty",
        "sixty", "seventy", "eighty", "ninety"
    };

    // Convert number to words (recursive)
    private static String convert(int num) {
        if (num == 0) return "zero";
        if (num < 20) return belowTwenty[num];
        if (num < 100) 
            return tens[num / 10] + (num % 10 != 0 ? " " + belowTwenty[num % 10] : "");
        if (num < 1000) 
            return belowTwenty[num / 100] + " hundred" + (num % 100 != 0 ? " " + convert(num % 100) : "");
        if (num < 100000) {
            return convert(num / 1000) + " thousand" + (num % 1000 != 0 ? " " + convert(num % 1000) : "");
        }
        return "";
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        while (true) {
            int num = sc.nextInt();
            if (num == 0) break; // stop reading input when 0
            System.out.println(convert(num));
        }

        sc.close();
    }
}

