public class DecryptMessage {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String encrypted = sc.nextLine();
        sc.close();

        // First char = number of columns
        int n = Character.getNumericValue(encrypted.charAt(0));
        String cipher = encrypted.substring(1);

        int length = cipher.length();
        int rows = (int) Math.ceil((double) length / n);

        // Fill column-wise
        char[][] grid = new char[rows][n];
        int index = 0;
        for (int col = 0; col < n; col++) {
            for (int row = 0; row < rows; row++) {
                if (index < length) {
                    grid[row][col] = cipher.charAt(index++);
                }
            }
        }

        // Read row-wise
        StringBuilder decrypted = new StringBuilder();
        for (int row = 0; row < rows; row++) {
            for (int col = 0; col < n; col++) {
                decrypted.append(grid[row][col]);
            }
        }

        // Replace * with space, remove trailing dots
        String result = decrypted.toString().replace('*', ' ').replaceAll("\\.+$", "");

        System.out.println(result);
    }
}

