import java.util.*;

public class PokerHand {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        List<String> cards = new ArrayList<>();

        for (int i = 0; i < 7; i++) {
            if (!sc.hasNext()) {
                System.out.println("Invalid input");
                return;
            }
            cards.add(sc.next().toLowerCase());
        }
        sc.close();

        // Valid ranks and suits
        Set<String> validRanks = new HashSet<>(Arrays.asList(
            "a","k","q","j","1","2","3","4","5","6","7","8","9","0"
        ));
        Set<Character> validSuits = new HashSet<>(Arrays.asList('s','c','d','h'));

        Map<Integer,Integer> rankCount = new HashMap<>();
        Map<Character,Integer> suitCount = new HashMap<>();
        List<Integer> ranks = new ArrayList<>();

        // Helper to convert rank  int
        Map<String,Integer> rankMap = new HashMap<>();
        rankMap.put("a",1); rankMap.put("j",11); rankMap.put("q",12);
        rankMap.put("k",13); rankMap.put("0",10);
        for (int i=2; i<=9; i++) rankMap.put(Integer.toString(i), i);
        rankMap.put("1",1); // allow "1" for Ace also

        // Validate and parse
        for (String card : cards) {
            if (card.length() < 2) {
                System.out.println("Invalid input");
                return;
            }
            String rank = card.substring(0, card.length()-1);
            char suit = card.charAt(card.length()-1);

            if (!validRanks.contains(rank) || !validSuits.contains(suit)) {
                System.out.println("Invalid input");
                return;
            }
            int value = rankMap.get(rank);
            ranks.add(value);
            rankCount.put(value, rankCount.getOrDefault(value,0)+1);
            suitCount.put(suit, suitCount.getOrDefault(suit,0)+1);
        }

        // Check Four of a kind
        for (int count : rankCount.values()) {
            if (count >= 4) {
                System.out.println("Four of a kind");
                return;
            }
        }

        // Check Flush
        for (int count : suitCount.values()) {
            if (count >= 5) {
                System.out.println("Flush");
                return;
            }
        }

        // Check Straight
        Set<Integer> uniq = new HashSet<>(ranks);
        List<Integer> sorted = new ArrayList<>(uniq);
        Collections.sort(sorted);
        int consec = 1, maxConsec = 1;
        for (int i = 1; i < sorted.size(); i++) {
            if (sorted.get(i) == sorted.get(i-1)+1) {
                consec++;
                maxConsec = Math.max(maxConsec, consec);
            } else {
                consec = 1;
            }
        }
        if (maxConsec >= 5) {
            System.out.println("Straight");
            return;
        }

        // Check Three of a kind
        for (int count : rankCount.values()) {
            if (count >= 3) {
                System.out.println("Three of a kind");
                return;
            }
        }

        // Check Two Pair
        int pairs = 0;
        for (int count : rankCount.values()) {
            if (count >= 2) pairs++;
        }
        if (pairs >= 2) {
            System.out.println("Two Pair");
            return;
        }

        // If nothing matched
        System.out.println("Invalid input");
    }
}

