import java.util.*;

public class MatrixGame {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int m = sc.nextInt(); // number of rows
        int n = sc.nextInt(); // number of columns

        int[][] matrix = new int[m][n];

        // Read matrix
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                matrix[i][j] = sc.nextInt();
            }
        }

        // New matrix with extra row and column
        int[][] result = new int[m + 1][n + 1];

        // Copy original values
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                result[i][j] = matrix[i][j];
            }
        }

        // Row sums
        for (int i = 0; i < m; i++) {
            int rowSum = 0;
            for (int j = 0; j < n; j++) {
                rowSum += matrix[i][j];
            }
            result[i][n] = rowSum; // last column
        }

        // Column sums
        for (int j = 0; j < n; j++) {
            int colSum = 0;
            for (int i = 0; i < m; i++) {
                colSum += matrix[i][j];
            }
            result[m][j] = colSum; // last row
        }

        // Bottom-right element
        int totalRowSum = 0, totalColSum = 0;
        for (int i = 0; i < m; i++) totalRowSum += result[i][n];
        for (int j = 0; j < n; j++) totalColSum += result[m][j];

        result[m][n] = (totalRowSum + totalColSum) / 2;

        // Print result
        for (int i = 0; i <= m; i++) {
            for (int j = 0; j <= n; j++) {
                System.out.print(result[i][j] + " ");
            }
            System.out.println();
        }
    }
}

