public class LargestTripleSum {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt();   // size of array

        if (n < 3) {
            System.out.println("Invalid Input");
            return;
        }

        int[] arr = new int[n];
        for (int i = 0; i < n; i++) {
            arr[i] = sc.nextInt();
        }

        int maxSum = 0;
        int index = 0;

        // compute first triple sum
        maxSum = arr[0] + arr[1] + arr[2];
        index = 0;

        // sliding window for remaining triples
        for (int i = 1; i <= n - 3; i++) {
            int sum = arr[i] + arr[i + 1] + arr[i + 2];
            if (sum > maxSum) {
                maxSum = sum;
                index = i;
            }
        }

        // print results
        System.out.println(maxSum);
        System.out.println(arr[index] + " " + arr[index + 1] + " " + arr[index + 2]);
    }
}

