import java.util.*;

public class SplitArrayEqualSum {

    // Function to check if subset with target sum exists
    static boolean canPartition(int[] arr, int n, int target) {
        boolean[][] dp = new boolean[n + 1][target + 1];

        // base case: sum 0 is always possible
        for (int i = 0; i <= n; i++) {
            dp[i][0] = true;
        }

        // fill dp table
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= target; j++) {
                if (arr[i - 1] <= j) {
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr[i - 1]];
                } else {
                    dp[i][j] = dp[i - 1][j];
                }
            }
        }
        return dp[n][target];
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int t = sc.nextInt();  // number of test cases

        while (t-- > 0) {
            int n = sc.nextInt();
            int[] arr = new int[n];
            int sum = 0;

            for (int i = 0; i < n; i++) {
                arr[i] = sc.nextInt();
                sum += arr[i];
            }

            // if total sum is odd  cannot split
            if (sum % 2 != 0) {
                System.out.println(-1);
                continue;
            }

            int target = sum / 2;

            if (canPartition(arr, n, target)) {
                System.out.println(target);
            } else {
                System.out.println(-1);
            }
        }

        sc.close();
    }
}

