import java.util.*;

public class TimeRange {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Read starting time
        int h1 = sc.nextInt();
        int m1 = sc.nextInt();
        int s1 = sc.nextInt();

        // Read step time
        int hStep = sc.nextInt();
        int mStep = sc.nextInt();
        int sStep = sc.nextInt();

        // Read n
        int n = sc.nextInt();

        // Multiply step by n
        int totalH = hStep * n;
        int totalM = mStep * n;
        int totalS = sStep * n;

        // Normalize seconds -> minutes
        totalM += totalS / 60;
        totalS = totalS % 60;

        // Normalize minutes -> hours
        totalH += totalM / 60;
        totalM = totalM % 60;

        // Now add step*n to starting time
        int resH = h1 + totalH;
        int resM = m1 + totalM;
        int resS = s1 + totalS;

        // Normalize result
        resM += resS / 60;
        resS = resS % 60;

        resH += resM / 60;
        resM = resM % 60;

        resH = resH % 24; // wrap around 24-hour format

        // Print in HH MM SS with leading zeros
        System.out.printf("%02d %02d %02d%n", resH, resM, resS);

        sc.close();
    }
}

