import java.util.*;

public class LongestSortedSequence {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int n = sc.nextInt();

        // validate array size
        if (n > 20 || n <= 0) {
            System.out.println("Array size of " + n + " is Invalid");
            return;
        }

        int[] arr = new int[n];
        for (int i = 0; i < n; i++) {
            arr[i] = sc.nextInt();
        }

        int maxLen = 1;
        int maxStart = 0;
        int currLen = 1;
        int currStart = 0;

        for (int i = 1; i < n; i++) {
            if (arr[i] >= arr[i - 1]) {
                currLen++;
            } else {
                if (currLen > maxLen) {
                    maxLen = currLen;
                    maxStart = currStart;
                }
                currLen = 1;
                currStart = i;
            }
        }

        // final check in case longest sequence ends at last element
        if (currLen > maxLen) {
            maxLen = currLen;
            maxStart = currStart;
        }

        // print result
        System.out.println(maxLen);
        for (int i = maxStart; i < maxStart + maxLen; i++) {
            System.out.println(arr[i]);
        }

        sc.close();
    }
}

