public class PalindromeCheck {

    // Recursive function to check if string is palindrome
    public static boolean isPalindrome(String s) {
        // base cases
        if (s.length() <= 1) {
            return true;  // single char or empty string is palindrome
        }

        // check first and last character
        if (s.charAt(0) != s.charAt(s.length() - 1)) {
            return false;
        }

        // recursive call on substring (excluding first and last characters)
        return isPalindrome(s.substring(1, s.length() - 1));
    }

    public static void main(String[] args) {
        String str1 = "madam";
        String str2 = "hello";

        System.out.println(str1 + " -> " + isPalindrome(str1)); // true
        System.out.println(str2 + " -> " + isPalindrome(str2)); // false
    }
}

