import java.util.*;

public class TimeInWords {

    // mapping of numbers to words
    static String[] words = {
        "zero", "one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten",
        "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen",
        "twenty", "twenty one", "twenty two", "twenty three", "twenty four", "twenty five",
        "twenty six", "twenty seven", "twenty eight", "twenty nine"
    };

    public static String convert(int h, int m) {
        if (m == 0) {
            return words[h] + " o' clock";
        } else if (m == 15) {
            return "quarter past " + words[h];
        } else if (m == 30) {
            return "half past " + words[h];
        } else if (m == 45) {
            return "quarter to " + words[(h % 12) + 1];
        } else if (m < 30) {
            return words[m] + (m == 1 ? " minute past " : " minutes past ") + words[h];
        } else {
            int remaining = 60 - m;
            return words[remaining] + (remaining == 1 ? " minute to " : " minutes to ") + words[(h % 12) + 1];
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int t = sc.nextInt(); // number of test cases
        sc.nextLine(); // consume newline

        for (int i = 0; i < t; i++) {
            String time = sc.nextLine();
            String[] parts = time.split(":");
            int h = Integer.parseInt(parts[0]);
            int m = Integer.parseInt(parts[1]);

            System.out.println(convert(h, m));
        }

        sc.close();
    }
}

