public class DecimalToBase {

    public static String dec2Base(int num, int base) {
        if (base < 2 || base > 16) {
            throw new IllegalArgumentException("Base must be between 2 and 16");
        }

        if (num == 0) return "0";   // special case

        String digits = "0123456789ABCDEF";
        StringBuilder result = new StringBuilder();

        int n = num;
        while (n > 0) {
            int remainder = n % base;
            result.append(digits.charAt(remainder));
            n /= base;
        }

        return result.reverse().toString();
    }

    public static void main(String[] args) {
        // Examples
        System.out.println(dec2Base(17, 2));   // 10001
        System.out.println(dec2Base(17, 8));   // 21
        System.out.println(dec2Base(26, 16));  // 1A
        System.out.println(dec2Base(255, 16)); // FF
        System.out.println(dec2Base(31, 2));   // 11111
    }
}

