        Scanner sc = new Scanner(System.in);

        long a = sc.nextLong();
        long b = sc.nextLong();

        // smallest integer whose 6th power >= a
        long start = (long) Math.ceil(Math.pow(a, 1.0/6));
        // largest integer whose 6th power <= b
        long end   = (long) Math.floor(Math.pow(b, 1.0/6));

        int count = 0;
        for (long i = start; i <= end; i++) {
            long val = i * i * i * i * i * i; // i^6
            if (val >= a && val <= b) {
                count++;
            }
        }

        System.out.println(count);
    }
}

