import java.util.Scanner;

public class TitleCaseWithAbbrev {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String sentence = sc.nextLine();
        sc.close();

        String[] words = sentence.split(" ");
        StringBuilder result = new StringBuilder();

        for (String word : words) {
            // Check if abbreviation (all uppercase)
            if (word.equals(word.toUpperCase())) {
                result.append(word).append(" ");
            } else {
                // Convert to Title Case (first letter uppercase + rest as is)
                if (word.length() > 0) {
                    String formatted = word.substring(0, 1).toUpperCase() + word.substring(1);
                    result.append(formatted).append(" ");
                }
            }
        }

        System.out.println(result.toString().trim());
    }
}

