import java.util.Scanner;

public class LychrelCheck {

    // Function to reverse a number
    public static long reverse(long num) {
        long rev = 0;
        while (num > 0) {
            rev = rev * 10 + num % 10;
            num /= 10;
        }
        return rev;
    }

    // Function to check palindrome
    public static boolean isPalindrome(long num) {
        return num == reverse(num);
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long num = sc.nextLong();
        sc.close();

        long temp = num;
        int iteration = 0;
        long fifthIterationValue = 0;
        boolean foundPalindrome = false;

        while (iteration < 10) {
            long rev = reverse(temp);
            temp = temp + rev;
            iteration++;

            if (iteration == 5) {
                fifthIterationValue = temp;
            }

            if (isPalindrome(temp)) {
                System.out.println(iteration); // Found palindrome
                foundPalindrome = true;
                break;
            }
        }

        if (!foundPalindrome) {
            System.out.println(num + " is a Lychrel Number");
            System.out.println("5th iteration of number " + num + " is " + fifthIterationValue);
        }
    }
}

