import java.util.*;

public class RomanToDecimal {

    // Map of Roman values
    private static final Map<Character, Integer> romanMap = new HashMap<>();
    static {
        romanMap.put('I', 1);
        romanMap.put('V', 5);
        romanMap.put('X', 10);
        romanMap.put('L', 50);
        romanMap.put('C', 100);
        romanMap.put('D', 500);
        romanMap.put('M', 1000);
    }

    // Method to check if Roman numeral is valid
    private static boolean isValidRoman(String s) {
        // Check for invalid characters
        for (char c : s.toCharArray()) {
            if (!romanMap.containsKey(c)) return false;
        }

        // Check repetition rules
        if (s.matches(".*(IIII|XXXX|CCCC|MMMM).*")) return false; // can't repeat >3
        if (s.matches(".*(VV|LL|DD).*")) return false;           // V, L, D can't repeat

        // Check invalid subtractive combinations
        if (s.matches(".*IL|IC|ID|IM.*")) return false;
        if (s.matches(".*VX|VL|VC|VD|VM.*")) return false;
        if (s.matches(".*XD|XM.*")) return false;
        if (s.matches(".*LC|LD|LM.*")) return false;
        if (s.matches(".*DM.*")) return false;

        return true;
    }

    // Convert Roman to Decimal
    private static int romanToDecimal(String s) {
        int n = s.length();
        int total = 0;

        for (int i = 0; i < n; i++) {
            int value = romanMap.get(s.charAt(i));

            if (i + 1 < n && romanMap.get(s.charAt(i + 1)) > value) {
                total -= value;  // subtractive case
            } else {
                total += value;
            }
        }
        return total;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String roman = sc.nextLine().trim();

        if (!isValidRoman(roman)) {
            System.out.println(roman + " is an invalid input");
        } else {
            int result = romanToDecimal(roman);
            System.out.println(result);
        }
    }
}

