import java.util.*;

public class SumOfFactors {

    // Method to calculate sum of factors of n
    public static long sumOfFactors(int n) {
        long sum = 0;
        int sqrt = (int)Math.sqrt(n);

        for (int i = 1; i <= sqrt; i++) {
            if (n % i == 0) {
                sum += i; // add divisor
                if (i != n / i) { // avoid adding sqrt twice
                    sum += n / i;
                }
            }
        }
        return sum;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int T = sc.nextInt();

        while (T-- > 0) {
            int n = sc.nextInt();
            System.out.println(sumOfFactors(n));
        }
    }
}
